import React, { useState, useEffect, useCallback } from "react";

export default function Intro() {
    const [selection, setSelection] = useState("");
    let directory = window.location.pathname;
    let filePath = directory.substring(0, directory.lastIndexOf('/'));

    function programSelector() {
        if (selection === "1") {
            return (
                <div>
                    <img src="./Four in a Row Example.gif" alt="A game of Connect-4." allowFullScreen />
                </div>
            )
        }
        else {
            return (
                <div>
                    <p>Nothing is happening!</p>
                    <p>{filePath}</p>
                </div>
            )
        }
    }

    const changeSelection = useCallback((select) => {
        setSelection(select.target.value);
    }, [setSelection]);

    return (
        <div className="container" id="main">
            <h3>Select a program: </h3>
            <select className="form-select" size="3" onChange={changeSelection}>
                <option value="1">Assignment 2 - Four in a Row Game</option>
                <option value="2">Assignment 5 - Hangman Game</option>
                <option value="3">Assignment 6 & 7 - Banking App</option>
            </select>
            <div>
                {programSelector()}
            </div>
        </div>
    );
}
